<script type="text/javascript">
var popUpResultWrapper = document.querySelector('.spin-result-wrapper');
var wheel = document.querySelector('.wheel-img');
document.querySelector('.close-popup').addEventListener('click', () => {
  popUpResultWrapper.style.display = 'none'
})
document.querySelector('.pop-up-button').addEventListener('click', () => {
  popUpResultWrapper.style.display = 'none'
})
document.querySelector('.wheel-cursor').addEventListener('click', () => {
  wheel.classList.add('super-rotation');
  setTimeout(() => {
    popUpResultWrapper.style.display = "block";
  }, 8000);
  setTimeout(() => {
    document.querySelector('.spin-wrapper').style.height = '0';
    document.querySelector('.spin-wrapper').style.opacity = '0';
    document.querySelector('.order_block').style.display = 'block';
    wheel.style.display = 'none';
  }, 10000)
})
</script>
<script>
const anchorst = document.querySelectorAll('a[href*="#"]')

for (let anchort of anchorst) {
anchort.addEventListener('click', function (e) {
e.preventDefault()
const blockID = anchort.getAttribute('href')

document.querySelector('' + blockID).scrollIntoView({
  behavior: 'smooth',
  block: 'start'
})
})
}
</script>
<script>
	function validateform(form) {
		if (form.elements["name"].value == "") {
			alert("يرجى إدخال اسمك الكامل");
			return false;
		}
		if (form.elements["name"].value.length < 2) {
			alert("يرجى إدخال اسمك الكامل");
			return false;
		}
		var phone_reg = new RegExp("[A-z А-я]", "g");
		var literInPhone = phone_reg.test(form.elements["phone"].value);
		if (form.elements["phone"].value == "") {
			alert("رقم الهاتف غير صحيح ، يرجى إعادة إدخاله");
			return false;
		}
		if (literInPhone) {
			alert("رقم الهاتف غير صحيح ، يرجى إعادة إدخاله");
			return false;
		}
		if (form.elements["phone"].value.length > 17) {
			alert("رقم الهاتف غير صحيح ، يرجى إعادة إدخاله");
			return false;
		}

		form.querySelector(".btn").setAttribute("disabled", true);
		form.submit();
	}
